import { injectable } from "@tsed/di";
/**
 * Create a new custom formats validator
 * @param token
 * @param name
 * @param options
 * @ajv
 */
export function formats(token, name, options = {}) {
    return injectable(token, { type: "ajv:formats" }).set("ajv:formats", {
        name,
        options
    });
}
