export function useDecorators(...decorators) {
    return (...args) => {
        const isMethod = typeof args[2] === "object";
        decorators
            .filter((o) => !!o)
            .forEach((decorator) => {
            const descriptor = decorator(...args);
            if (isMethod && typeof descriptor === "object") {
                args[2] = descriptor;
            }
        });
        if (isMethod) {
            return args[2];
        }
    };
}
