import Http from "node:http";
import { configuration, constant } from "@tsed/di";
import { createServer } from "./createServer.js";
export function createHttpServer(requestListener) {
    const httpOptions = configuration().get("httpOptions");
    return createServer({
        port: constant("httpPort"),
        type: "http",
        token: Http.Server,
        server: () => Http.createServer(httpOptions, requestListener)
    });
}
