import { OpenSpecTag } from "@tsed/openspec";
/**
 * Add metadata tags to the decorated element (class or method).
 *
 * ## Examples
 * ### On method
 *
 * ```typescript
 * @Controller("/")
 * class MyController {
 *  @Tags("api")
 *  get() {}
 * }
 * ```
 * ### On Class
 *
 * ```typescript
 * @Controller("/")
 * @Tags("api")
 * class MyController {
 *  get() {}
 * }
 * ```
 *
 * @param tags
 * @decorator
 * @swagger
 * @schema
 * @classDecorator
 * @operation
 */
export declare function Tags(...tags: (string | OpenSpecTag)[]): ClassDecorator & MethodDecorator;
